DROP TABLE DICT;
CREATE TABLE DICT ( ID BIGINT NOT NULL, CODE VARCHAR(30) NOT NULL, NAME VARCHAR(30) NOT NULL, PARENT_CODE VARCHAR(30), VALUE VARCHAR(30), CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, PRIMARY KEY (ID) );

DROP TABLE GAME;
CREATE TABLE GAME ( ID BIGINT NOT NULL, TITLE VARCHAR(50) NOT NULL, PRIMARY KEY (ID) );

DROP TABLE HIBERNATE_SEQUENCES;
CREATE TABLE HIBERNATE_SEQUENCES ( SEQUENCE_NAME VARCHAR(255) NOT NULL, NEXT_VAL BIGINT, PRIMARY KEY (SEQUENCE_NAME) );

DROP TABLE ID_GEN;
CREATE TABLE ID_GEN ( SEQUENCE_NAME VARCHAR(255) NOT NULL, NEXT_VAL BIGINT, ID VARCHAR(254), SEQUENCE_VALUE BIGINT, PRIMARY KEY (SEQUENCE_NAME) );

DROP TABLE ID_GEN1;
CREATE TABLE ID_GEN1 ( ID VARCHAR(254) NOT NULL, SEQUENCE_VALUE BIGINT, PRIMARY KEY (ID) );

DROP TABLE OPENJPA_SEQUENCE_TABLE;
CREATE TABLE OPENJPA_SEQUENCE_TABLE ( ID SMALLINT NOT NULL, SEQUENCE_VALUE BIGINT, PRIMARY KEY (ID) );

DROP TABLE PERMISSION;
CREATE TABLE PERMISSION ( ID BIGINT NOT NULL, PERMISSION VARCHAR(100) NOT NULL, PERMISSION_NAME VARCHAR(40), ROLE_ID BIGINT, PRIMARY KEY (ID) );

DROP TABLE PERSON;
CREATE TABLE PERSON ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, ADDRESS VARCHAR(100), BIRTHDAY TIMESTAMP, EMAIL VARCHAR(50), NAME VARCHAR(50) NOT NULL, NATIONALITY VARCHAR(50), PHONE VARCHAR(20), REMARKS VARCHAR(500), SEX VARCHAR(10), VIP SMALLINT, PRIMARY KEY (ID) );

DROP TABLE PRODUCT;
CREATE TABLE PRODUCT ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, AVAILABLE SMALLINT, CATEGORY VARCHAR(255), CODE VARCHAR(255), DESCRIBE VARCHAR(1000), NAME VARCHAR(255), PRICE DOUBLE, PRODUCTION_DATE TIMESTAMP, PRIMARY KEY (ID) );

DROP TABLE REPORTFILE;
CREATE TABLE REPORTFILE ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, FILE_NAME VARCHAR(200) NOT NULL, FILE_STORE_LOCATION VARCHAR(400) NOT NULL, REMARKS VARCHAR(600), UPLOADED_BY VARCHAR(30) NOT NULL, UPLOADED_DATETIME TIMESTAMP NOT NULL, USE_IND SMALLINT, VERSION_NO INTEGER, REPORTMSTR_ID BIGINT, PRIMARY KEY (ID) );

DROP TABLE REPORTMSTR;
CREATE TABLE REPORTMSTR ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, REMARKS VARCHAR(300), REPORT_CATEGORY VARCHAR(20), REPORT_CODE VARCHAR(50), REPORT_CONFIG VARCHAR(600), REPORT_MODE VARCHAR(20), REPORT_NAME VARCHAR(200) NOT NULL, REPORT_NAME_LANG VARCHAR(200), PRIMARY KEY (ID) );

DROP TABLE REPORTPARAMETER;
CREATE TABLE REPORTPARAMETER ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, DEFAULT_VALUE VARCHAR(200), DEFAULT_VALUE_TYPE VARCHAR(20), DISPLAY_IND SMALLINT, EDIT_IND SMALLINT, JAVA_DATA_TYPE VARCHAR(20) NOT NULL, MANDATORY_IND SMALLINT, REMARKS VARCHAR(600), REPORTPARAMETER_CODE VARCHAR(30) NOT NULL, SQL_STATEMENT VARCHAR(2000), UI_LABEL VARCHAR(50), UI_LABEL_LANG VARCHAR(50), UI_TYPE VARCHAR(20) NOT NULL, REPORTMSTR_ID BIGINT, PRIMARY KEY (ID) );

DROP TABLE REPORTROLE;
CREATE TABLE REPORTROLE ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, REPORTMSTR_ID BIGINT, ROLE_ID BIGINT, PRIMARY KEY (ID) );

DROP TABLE RESOURCE;
CREATE TABLE RESOURCE ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, IS_LEAF SMALLINT, ISMENU SMALLINT, KEY_NAME VARCHAR(255) NOT NULL, LEVEL INTEGER NOT NULL, NAME VARCHAR(40), NUMBER VARCHAR(255), PARENT_ID BIGINT, URL VARCHAR(100), TYPE VARCHAR(254), URI VARCHAR(254), PRIMARY KEY (ID) );

DROP TABLE REVINFO;
CREATE TABLE REVINFO ( REV INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY, REVTSTMP BIGINT, PRIMARY KEY (REV) );

DROP TABLE ROLE;
CREATE TABLE ROLE ( ID BIGINT NOT NULL, IS_ADMIN SMALLINT, DESCRIPTION VARCHAR(255), ROLE_NAME VARCHAR(255), ROLE_SCOPE VARCHAR(255), STATE INTEGER NOT NULL, PRIMARY KEY (ID) );

DROP TABLE ROLE_RESOURCE;
CREATE TABLE ROLE_RESOURCE ( ID BIGINT NOT NULL, RESOURCE_ID BIGINT, ROLE_ID BIGINT, PRIMARY KEY (ID) );

DROP TABLE STOREDLOG;
CREATE TABLE STOREDLOG ( ID BIGINT NOT NULL, LOGLEVEL VARCHAR(255), LOGDTTM TIMESTAMP, THROWABLEMESSAGE VARCHAR(255), USERMESSAGE VARCHAR(255), WHERECLASS VARCHAR(255), PRIMARY KEY (ID) );

DROP TABLE TESTUSER;
CREATE TABLE TESTUSER ( ID VARCHAR(255) NOT NULL, FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), PRIMARY KEY (ID) );

DROP TABLE USERS;
CREATE TABLE USERS ( ID BIGINT NOT NULL, CREATED_BY VARCHAR(30), CREATED_DATETIME TIMESTAMP, DEFUNCT_IND SMALLINT, UPDATED_BY VARCHAR(30), UPDATED_DATETIME TIMESTAMP, ADDRESS VARCHAR(100), CELL_PHONE VARCHAR(20), EMAIL VARCHAR(60), LOGIN_NAME VARCHAR(30) NOT NULL, TELEPHONE VARCHAR(20), USER_NAME VARCHAR(30) NOT NULL, USER_PASS VARCHAR(20) NOT NULL, CELLPHONE_NO VARCHAR(30), CONTACT_ADDRESS VARCHAR(60), CONTACT_TELEPHONE_NO VARCHAR(30), NAME VARCHAR(30), PURCHASE_ENTITY_NAME VARCHAR(60), PURCHASE_LOC_NAME VARCHAR(60), PRIMARY KEY (ID) );

DROP TABLE USER_ROLE;
CREATE TABLE USER_ROLE ( ID BIGINT NOT NULL, ROLE_ID BIGINT, USER_ID BIGINT, PRIMARY KEY (ID) );

